import gzip
import math
# with help from https://github.com/outbreak-info/outbreak.info/blob/0b04f4a52d31ea89e66f5611056bcad921a11fa5/web/src/assets/genomics/sarscov2_NC045512_genes_nt.json#L4
genes = {'ORF1a': [266, 13467], 'ORF1b': [13468, 21556], 'S': [21563, 25385], 'ORF3a': [25393, 26221], 'E': [26245, 26473], 'M': [26523, 27192], 'ORF6': [27202, 27388], 'ORF7a': [27394, 27760], 'ORF7b': [27756, 27888], 'ORF8': [27894, 28260], 'N': [28274, 29534], 'ORF10': [29558, 29675]}




seq = """"""
seq = seq.upper()

bases = "TCAG"
codons = [a + b + c for a in bases for b in bases for c in bases]
amino_acids = 'FFLLSSSSYY**CC*WLLLLPPPPHHQQRRRRIIIMTTTTNNKKSSRRVVVVAAAADDEEGGGG'
codon_table = dict(zip(codons, amino_acids))


def get_aa_sub(pos,ref,alt):
    assert ref == seq[pos-1]
    for gene_name, gene_range in genes.items():
        if pos>=gene_range[0] and pos<=gene_range[1]:
            aa_loc = math.floor((pos - gene_range[0])/3)+1
            frame = (int(pos - gene_range[0])) % 3

            original_codon = seq[pos-1 -frame:pos-1+3-frame]
            new_codon = list(original_codon)
            new_codon[frame]= alt
            new_codon = "".join(new_codon)

            if codon_table[original_codon] != codon_table[new_codon]:
                return f"{gene_name}:{codon_table[original_codon]}{aa_loc}{codon_table[new_codon]}"
    return ""

headers_to_col_number =None
import tqdm
for line in tqdm.tqdm(gzip.open("./public-latest.all.masked.vcf.gz", "rt")):
    line = line.strip()

    cols = line.split("\t")
    if line.startswith("#CHROM"):
        headers = cols
        headers_to_col_number = {x:i for i,x in enumerate(cols)}
        continue
    if line.startswith("#"):
        continue

    pos = int(cols[headers_to_col_number['POS']])

    ref= cols[headers_to_col_number['REF']]
    alts = cols[headers_to_col_number['ALT']].split(",")

    for column in range(9,len(cols)):
        value = int(cols[column])
        if value==0:
            continue
        else:
            alt = alts[value-1]
            aa_sub = get_aa_sub(pos,ref,alt)
            print("\t".join([headers[column], str(pos),ref,alt,aa_sub ]))



